import{validateFiles}from"./validations.min.js";document.addEventListener("DOMContentLoaded",()=>{const e=document.forms.frmRegisterThumb,i=e.thumb;e.addEventListener("submit",a=>{a.preventDefault();const s=i.parentNode.querySelector("span.invalid-feedback");validateFiles(i.files)?e.submit():0===i.files.length?(s.innerHTML="Please, select an image!",i.classList.add("is-invalid")):(s.innerHTML="Please, select a valid image!",i.classList.add("is-invalid"))})});