const emailRegex=/^([a-zA-Z0-9\.\+\-\_]{5,60})\@([a-zA-Z0-9\.\+\-\_]{2,10})\.([a-zA-Z0-9]{2,10}).+$/,passwordRegex=/([a-zA-Z0-9]){2,60}/,nameRegex=/([a-zA-Z]){2,60}/;function validateEmail(e){return emailRegex.test(e)&&e.length<=100&&e.length>=9}function validatePassword(e){return passwordRegex.test(e)&&e.length<60&&e.length>4}function validateName(e){return nameRegex.test(e)&&e.length<60&&e.length>2}export{validateEmail,validatePassword,validateName};